import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';
import './App.css';

const socket = io('http://localhost:4000');
const USERS = ['Usuario 1', 'Usuario 2', 'Usuario 3', 'Usuario 4'];

function App() {
  const [user, setUser] = useState(null);
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [message, setMessage] = useState('');
  const [notifications, setNotifications] = useState([]);
  const [notificationType, setNotificationType] = useState('global'); // 'global' or 'private'
  const [unreadNotifications, setUnreadNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [isConnected, setIsConnected] = useState(false);

  useEffect(() => {
    socket.on('user_update', (users) => {
      setUsers(users);
    });

    socket.on('notification', (notification) => {
      setNotifications((prevNotifications) => [...prevNotifications, notification]);
      setUnreadNotifications((prev) => [...prev, notification]);
      setUnreadCount((prev) => prev + 1);
    });

    return () => {
      socket.off('user_update');
      socket.off('notification');
    };
  }, []);

  const handleUserClick = (username) => {
    setUser(username);
    socket.emit('user_connected', username);
    setIsConnected(true);
    setUnreadCount(0);
  };

  const handleUserSelect = (selectedUser) => {
    setSelectedUser(selectedUser);
    setUnreadCount(0);  // Reset unread notifications for the selected user
  };

  const handleSendNotification = () => {
    if (message.trim()) {
      const notification = {
        sender: user,
        message: message,
        type: notificationType,
        receiver: notificationType === 'private' ? selectedUser : 'all',
        timestamp: new Date().toLocaleTimeString(),
      };

      socket.emit('send_notification', notification);
      setMessage('');
      setUnreadCount(0);  // Reset unread notifications when sending one
    }
  };

  const handleNotificationTypeChange = (type) => {
    setNotificationType(type);
    if (type === 'private') {
      setUnreadCount(0);  // Reset unread count if private is selected
    }
  };

  const handleBellClick = () => {
    setNotifications((prev) => [...prev, ...unreadNotifications]);
    setUnreadNotifications([]);
    setUnreadCount(0);
  };

  const toggleConnection = () => {
    if (isConnected) {
      socket.emit('disconnectUser', user);
      setIsConnected(false);
      setUnreadCount(unreadNotifications.length);
    } else {
      socket.emit('connectUser', user);
      setIsConnected(true);
      setUnreadCount(0);
    }
  };

  return (
    <div className="flex h-screen">
      {!user ? (
        <div className="flex justify-center items-center w-full">
          <div className="text-center">
            <h2 className="text-2xl font-semibold mb-4">Selecciona tu usuario</h2>
            {USERS.map((username) => (
              <button
                key={username}
                className="bg-blue-500 text-white py-2 px-4 rounded-md m-2"
                onClick={() => handleUserClick(username)}
              >
                {username}
              </button>
            ))}
          </div>
        </div>
      ) : (
        <>
          <div className="w-1/4 bg-gray-100 border-r border-gray-200">
            <div className="flex items-center justify-between p-4 border-b border-gray-200">
              <div className="flex items-center">
                <div className="font-semibold">{user}</div>
                <div
                  className="status-indicator"
                  style={{ backgroundColor: isConnected ? 'green' : 'red' }}
                ></div>
                <button className="connect-button" onClick={toggleConnection}>
                  {isConnected ? 'Desconectar' : 'Conectar'}
                </button>
              </div>
            </div>
            <div className="p-4">
              <h2 className="font-semibold mb-2">Usuarios Conectados</h2>
              {users.map(
                (u) =>
                  u !== user && (
                    <div
                      key={u}
                      className="flex items-center p-2 bg-purple-100 rounded-lg cursor-pointer"
                      onClick={() => handleUserSelect(u)}
                    >
                      <span className="font-semibold">{u}</span>
                    </div>
                  )
              )}
            </div>
          </div>

          <div className="flex-1 flex flex-col">
            <div className="flex items-center justify-between p-4 border-b border-gray-200">
              <div className="font-semibold text-xl">Notificaciones</div>
              <div
                className="bell-icon cursor-pointer"
                onClick={handleBellClick}
              >
                <i className="fas fa-bell"></i>
              </div>
              {unreadCount > 0 && (
                <div className="notification-count">{unreadCount}</div>
              )}
            </div>

            <div className="chat-box overflow-y-auto">
              {notifications.map((notif, index) => (
                <div key={index} className="notification">
                  <div>{notif.message}</div>
                  <div className="timestamp">{notif.timestamp}</div>
                </div>
              ))}
            </div>

            <div className="notification-input">
              <textarea
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                placeholder="Escribe una notificación..."
              />
              <div>
                <button onClick={() => handleNotificationTypeChange('global')}>
                  Global
                </button>
                <button onClick={() => handleNotificationTypeChange('private')}>
                  Privada
                </button>
              </div>
              {notificationType === 'private' && selectedUser && (
                <div className="selected-user">Enviando a: {selectedUser}</div>
              )}
              <button onClick={handleSendNotification}>Enviar Notificación</button>
            </div>
          </div>
        </>
      )}
    </div>
  );
}

export default App;
