import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';
import './App.css';

const socket = io('http://localhost:4000');

function App() {
  const [user, setUser] = useState(null);
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [chatMessages, setChatMessages] = useState([]);
  const [message, setMessage] = useState('');
  const [unreadNotifications, setUnreadNotifications] = useState(0);

  useEffect(() => {
    socket.on('user_update', (users) => {
      setUsers(users);
    });

    socket.on('chat_message', (message) => {
      if (
        (message.receiver === user && message.sender === selectedUser) ||
        (message.sender === user && message.receiver === selectedUser)
      ) {
        setChatMessages((prevMessages) => [...prevMessages, message]);
      }
    });

    return () => {
      socket.off('user_update');
      socket.off('chat_message');
    };
  }, [user, selectedUser]);

  const handleUserClick = (username) => {
    setUser(username);
    socket.emit('user_connected', username);
  };

  const handleUserSelect = (selectedUser) => {
    setSelectedUser(selectedUser);
    setChatMessages([]); // Limpiar mensajes previos
  };

  const handleSendMessage = () => {
    if (message.trim() && selectedUser) {
      const chatMessage = {
        sender: user,
        receiver: selectedUser,
        message: message,
        timestamp: new Date().toLocaleTimeString(),
      };
      socket.emit('send_chat_message', chatMessage);
      setMessage('');
      setChatMessages((prevMessages) => [...prevMessages, chatMessage]);
    }
  };

  return (
    <div className="App">
      {!user ? (
        <div className="user-selection">
          <h2>Selecciona tu usuario</h2>
          <button className="user-button" onClick={() => handleUserClick('Usuario 1')}>
            Usuario 1
          </button>
          <button className="user-button" onClick={() => handleUserClick('Usuario 2')}>
            Usuario 2
          </button>
          <button className="user-button" onClick={() => handleUserClick('Usuario 3')}>
            Usuario 3
          </button>
        </div>
      ) : (
        <div className="dashboard">
          <div className="sidebar">
            <h2 className="sidebar-title">Usuarios Conectados</h2>
            <div className="users-list">
              {users.map(
                (u) =>
                  u !== user && (
                    <div
                      key={u}
                      className="user-item"
                      onClick={() => handleUserSelect(u)}
                    >
                      <span className="user-name">{u}</span>
                    </div>
                  )
              )}
            </div>
          </div>
          <div className="chat-container">
            <div className="chat-header">
              {selectedUser ? (
                <h3>Chat con {selectedUser}</h3>
              ) : (
                <h3>Selecciona un usuario para empezar a chatear</h3>
              )}
            </div>
            {selectedUser && (
              <div className="chat-box">
                {chatMessages.map((msg, idx) => (
                  <div
                    key={idx}
                    className={msg.sender === user ? 'my-message' : 'other-message'}
                  >
                    <strong>{msg.sender}:</strong> {msg.message}{' '}
                    <span className="timestamp">{msg.timestamp}</span>
                  </div>
                ))}
              </div>
            )}
            {selectedUser && (
              <div className="chat-input">
                <textarea
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  placeholder="Escribe tu mensaje..."
                />
                <button onClick={handleSendMessage}>Enviar</button>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
