import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:4000');  // Conexi贸n al servidor de Node.js

function App() {
  const [notifications, setNotifications] = useState([]);

  useEffect(() => {
    // Escuchar el evento 'receive_notification' para recibir notificaciones en tiempo real
    socket.on('receive_notification', (notification) => {
      setNotifications((prevNotifications) => [
        ...prevNotifications,
        notification
      ]);
    });

    // Limpiar la conexi贸n cuando el componente se desmonte
    return () => {
      socket.off('receive_notification');
    };
  }, []);

  return (
    <div className="App">
      <h1>Notificaciones en Tiempo Real</h1>
      <div>
        {notifications.map((notification, index) => (
          <div key={index} className="notification">
            <p>{notification.message}</p>
            <small>{new Date(notification.createdAt).toLocaleTimeString()}</small>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
