// App.js
import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';
import './App.css';

const socket = io('http://localhost:4000');

function App() {
  const [user, setUser] = useState(null);
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [chatMessages, setChatMessages] = useState([]);
  const [message, setMessage] = useState('');
  const [newMessages, setNewMessages] = useState([]);

  useEffect(() => {
    socket.on('user_update', (users) => {
      setUsers(users);
    });

    socket.on('new_messages', (messages) => {
      setNewMessages(messages);
      setChatMessages((prevMessages) => [...prevMessages, ...messages]);
    });

    socket.on('chat_message', (message) => {
      if (message.receiver === user || message.sender === user) {
        setChatMessages((prevMessages) => [...prevMessages, message]);
      }
    });

    return () => {
      socket.off('user_update');
      socket.off('new_messages');
      socket.off('chat_message');
    };
  }, [user]);

  const handleUserClick = (username) => {
    setUser(username);
    socket.emit('user_connected', username);
  };

  const handleUserSelect = (selectedUser) => {
    setSelectedUser(selectedUser);
    setChatMessages([]);
  };

  const handleSendMessage = () => {
    if (message.trim() && selectedUser) {
      const chatMessage = {
        sender: user,
        receiver: selectedUser,
        message: message,
        timestamp: new Date().toLocaleTimeString(),
      };
      socket.emit('send_chat_message', chatMessage);
      setMessage('');
      setChatMessages((prevMessages) => [...prevMessages, chatMessage]);
    }
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      {!user ? (
        <div className="flex justify-center items-center w-full">
          <div className="text-center">
            <h2 className="text-2xl font-semibold mb-4">Selecciona tu usuario</h2>
            <button
              className="bg-blue-500 text-white py-2 px-4 rounded-md m-2"
              onClick={() => handleUserClick('Usuario 1')}
            >
              Usuario 1
            </button>
            <button
              className="bg-blue-500 text-white py-2 px-4 rounded-md m-2"
              onClick={() => handleUserClick('Usuario 2')}
            >
              Usuario 2
            </button>
            <button
              className="bg-blue-500 text-white py-2 px-4 rounded-md m-2"
              onClick={() => handleUserClick('Usuario 3')}
            >
              Usuario 3
            </button>
          </div>
        </div>
      ) : (
        <>
          <div className="w-1/4 bg-gray-100 border-r border-gray-200">
            <div className="flex items-center justify-between p-4 border-b border-gray-200">
              <div className="flex items-center">
                <img
                  src="https://placehold.co/40x40"
                  alt="User avatar"
                  className="rounded-full"
                />
                <div className="ml-2">
                  <div className="font-semibold">{user}</div>
                  <div className="text-sm text-gray-500">Activo</div>
                </div>
              </div>
              <i className="fas fa-plus text-purple-500"></i>
            </div>
            <div className="p-4">
              <h2 className="font-semibold mb-2">Usuarios Conectados</h2>
              {users.map(
                (u) =>
                  u !== user && (
                    <div
                      key={u}
                      className="flex items-center p-2 bg-purple-100 rounded-lg cursor-pointer"
                      onClick={() => handleUserSelect(u)}
                    >
                      <img
                        src="https://placehold.co/40x40"
                        alt="User avatar"
                        className="rounded-full"
                      />
                      <div className="ml-2">
                        <div className="font-semibold">{u}</div>
                        <div className="text-sm text-gray-500">Hola!</div>
                      </div>
                    </div>
                  )
              )}
            </div>
          </div>
          {/* Chat Area */}
          <div className="flex-1 flex flex-col">
            <div className="flex items-center justify-between p-4 border-b border-gray-200">
              {selectedUser ? (
                <div className="flex items-center">
                  <img
                    src="https://placehold.co/40x40"
                    alt="User avatar"
                    className="rounded-full"
                  />
                  <div className="ml-2">
                    <div className="font-semibold">{selectedUser}</div>
                  </div>
                </div>
              ) : (
                <h3>Selecciona un usuario para empezar a chatear</h3>
              )}
              <i className="fas fa-info-circle text-purple-500"></i>
            </div>
            <div className="flex-1 p-4 overflow-y-auto">
              {chatMessages.map((msg, idx) => (
                <div
                  key={idx}
                  className={`mb-4 ${
                    msg.sender === user ? 'text-right' : 'text-left'
                  }`}
                >
                  <div
                    className={`inline-block p-2 rounded-lg ${
                      msg.sender === user ? 'bg-purple-500 text-white' : 'bg-gray-200 text-black'
                    }`}
                  >
                    <strong>{msg.sender}:</strong> {msg.message}{' '}
                    <span className="text-sm text-gray-500 ml-2">{msg.timestamp}</span>
                  </div>
                </div>
              ))}
            </div>
            <div className="p-4 border-t border-gray-200">
              <div className="flex items-center">
                <input
                  type="text"
                  placeholder="Escribe tu mensaje..."
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  className="flex-1 p-2 border border-gray-300 rounded-lg focus:outline-none focus:border-purple-500"
                />
                <button
                  onClick={handleSendMessage}
                  className="bg-blue-500 text-white py-2 px-4 rounded-md ml-2"
                >
                  Enviar
                </button>
              </div>
            </div>
          </div>
        </>
      )}
    </div>
  );
}

export default App;
