import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';
import './App.css';

const socket = io('http://localhost:4000');
const USERS = ['Usuario 1', 'Usuario 2', 'Usuario 3', 'Usuario 4'];

function App() {
  const [user, setUser] = useState(null);
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [message, setMessage] = useState('');
  const [notifications, setNotifications] = useState([]);
  const [notificationType, setNotificationType] = useState('global'); // 'global' or 'private'
  const [unreadNotifications, setUnreadNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [isConnected, setIsConnected] = useState(false);

  useEffect(() => {
    // Obtener notificaciones desde el backend al cargar la p치gina
    fetch('http://localhost:4000/notifications')
      .then((res) => res.json())
      .then((data) => {
        setNotifications(data);
        setUnreadCount(data.length);  // Contar las notificaciones sin leer
      })
      .catch((err) => console.error('Error fetching notifications:', err));

    socket.on('user_update', (users) => {
      setUsers(users);
    });

    socket.on('notification', (notification) => {
      setNotifications((prevNotifications) => [...prevNotifications, notification]);
      setUnreadNotifications((prev) => [...prev, notification]);
      setUnreadCount((prev) => prev + 1);
    });

    return () => {
      socket.off('user_update');
      socket.off('notification');
    };
  }, []);

  const handleUserClick = (username) => {
    setUser(username);
    socket.emit('user_connected', username);
    setIsConnected(true);
    setUnreadCount(0);
  };

  const handleUserSelect = (selectedUser) => {
    setSelectedUser(selectedUser);
    setUnreadCount(0);  // Reset unread notifications for the selected user
  };

  const handleSendNotification = () => {
    if (message.trim()) {
      const notification = {
        sender: user,
        message: message,
        type: notificationType,
        receiver: notificationType === 'private' ? selectedUser : 'all',
        timestamp: new Date().toLocaleTimeString(),
      };

      socket.emit('send_notification', notification);
      setMessage('');
      setUnreadCount(0);  // Reset unread notifications when sending one
    }
  };

  const handleNotificationTypeChange = (type) => {
    setNotificationType(type);
    if (type === 'private') {
      setUnreadCount(0);  // Reset unread count if private is selected
    }
  };

  const handleBellClick = () => {
    setNotifications((prev) => [...prev, ...unreadNotifications]);
    setUnreadNotifications([]);
    setUnreadCount(0);
  };

  const toggleConnection = () => {
    if (isConnected) {
      socket.emit('disconnectUser', user);
      setIsConnected(false);
      setUnreadCount(unreadNotifications.length);
    } else {
      socket.emit('connectUser', user);
      setIsConnected(true);
      setUnreadCount(0);
    }
  };

  return (
    <div className="flex h-screen">
      {!user ? (
        <div className="flex justify-center items-center w-full">
          <div className="text-center">
            <h2 className="text-2xl font-semibold mb-4">Selecciona tu usuario</h2>
            {USERS.map((username) => (
              <button
                key={username}
                className="bg-blue-500 text-white py-2 px-4 rounded-md m-2"
                onClick={() => handleUserClick(username)}
              >
                {username}
              </button>
            ))}
          </div>
        </div>
      ) : (
        <>
          <div className="w-1/4 bg-gray-100 border-r border-gray-200">
            <div className="flex items-center justify-between p-4 border-b border-gray-200">
              <div className="flex items-center">
                <div className="font-semibold">{user}</div>
                <div
                  className="status-indicator"
                  style={{ backgroundColor: isConnected ? 'green' : 'red' }}
                ></div>
                <button className="connect-button" onClick={toggleConnection}>
                  {isConnected ? 'Desconectar' : 'Conectar'}
                </button>
              </div>
            </div>
            <div className="p-4">
              <h2 className="font-semibold mb-2">Usuarios Conectados</h2>
              {users.map(
                (u) =>
                  u !== user && (
                    <div
                      key={u}
                      className="flex items-center p-2 cursor-pointer"
                      onClick={() => handleUserSelect(u)}
                    >
                      <div>{u}</div>
                    </div>
                  )
              )}
            </div>
            <div className="p-4">
              <h2 className="font-semibold mb-2">Notificaciones</h2>
              <div
                className="notification-bell cursor-pointer"
                onClick={handleBellClick}
              >
                游띑勇끂r
                {unreadCount > 0 && (
                  <div className="badge">{unreadCount}</div>
                )}
              </div>
              {notifications.map((n, index) => (
                <div key={index} className="notification-item">
                  <div>
                    <strong>{n.sender}</strong>: {n.message} ({n.timestamp})
                  </div>
                </div>
              ))}
            </div>
          </div>
          <div className="w-3/4 p-8">
            <h2 className="text-2xl font-semibold mb-4">Enviar Notificaci칩n</h2>
            <div>
              <label>Tipo de notificaci칩n:</label>
              <div>
                <button onClick={() => handleNotificationTypeChange('global')}>
                  Global
                </button>
                <button onClick={() => handleNotificationTypeChange('private')}>
                  Privada
                </button>
              </div>
            </div>
            {notificationType === 'private' && (
              <div>
                <label>Selecciona el destinatario:</label>
                <select
                  value={selectedUser}
                  onChange={(e) => setSelectedUser(e.target.value)}
                >
                  <option value="">Seleccionar Usuario</option>
                  {users.map((u) => u !== user && <option key={u} value={u}>{u}</option>)}
                </select>
              </div>
            )}
            <div>
              <textarea
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                placeholder="Escribe tu mensaje"
              />
            </div>
            <button
              className="bg-blue-500 text-white py-2 px-4 rounded-md mt-4"
              onClick={handleSendNotification}
            >
              Enviar Notificaci칩n
            </button>
          </div>
        </>
      )}
    </div>
  );
}

export default App;
