import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';
import './App.css';

const socket = io('http://localhost:4000');

function App() {
    const [user, setUser] = useState(null);
    const [users, setUsers] = useState([]);
    const [message, setMessage] = useState('');
    const [notifications, setNotifications] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [chatMessages, setChatMessages] = useState([]);
    const [unreadNotifications, setUnreadNotifications] = useState(0);
    const [showNotifications, setShowNotifications] = useState(false);

    // Conectarse al servidor cuando el componente se monte
    useEffect(() => {
        // Escuchar actualizaciones de los usuarios conectados
        socket.on('user_update', (users) => {
            setUsers(users);
        });

        // Escuchar notificaciones
        socket.on('notification', (notification) => {
            setNotifications((prevNotifications) => {
                const newNotifications = [notification, ...prevNotifications];
                setUnreadNotifications((prev) => prev + 1); // Incrementar notificaciones no leídas
                return newNotifications.slice(0, 10); // Limitar a 10 notificaciones
            });
        });

        // Escuchar mensajes de chat
        socket.on('chat_message', (message) => {
            if (
                (message.receiver === user && message.sender === selectedUser) ||
                (message.sender === user && message.receiver === selectedUser)
            ) {
                setChatMessages((prevMessages) => [...prevMessages, message]);
            }
        });

        // Limpiar los listeners cuando el componente se desmonte
        return () => {
            socket.off('user_update');
            socket.off('notification');
            socket.off('chat_message');
        };
    }, [user, selectedUser]);

    // Función para manejar la selección del usuario
    const handleUserSelect = (selectedUser) => {
        setSelectedUser(selectedUser);
        setChatMessages([]); // Limpiar chat anterior
        setUnreadNotifications(0); // Resetear notificaciones no leídas al seleccionar chat
    };

    // Función para manejar el click en un botón de usuario
    const handleUserClick = (username) => {
        setUser(username);
        socket.emit('user_connected', username); // Emitir al backend que el usuario se ha conectado
    };

    // Función para manejar el envío de mensajes
    const handleSendMessage = () => {
        if (message.trim() && selectedUser) {
            const chatMessage = {
                sender: user,
                receiver: selectedUser,
                message: message,
                timestamp: new Date().toLocaleTimeString(),
            };
            socket.emit('send_chat_message', chatMessage); // Enviar el mensaje al backend
            setMessage('');
            setChatMessages((prevMessages) => [...prevMessages, chatMessage]); // Agregar mensaje localmente
        }
    };

    // Función para manejar el click en el ícono de notificación
    const handleNotificationClick = () => {
        setShowNotifications(!showNotifications);
        setUnreadNotifications(0); // Marcar todas como leídas
    };

    // Función para desconectar
    const handleDisconnect = () => {
        socket.emit('user_disconnect');
        setUser(null);
        setSelectedUser(null);
        setChatMessages([]);
        setNotifications([]);
    };

    return (
        <div className="App">
            {!user ? (
                <div className="user-selection">
                    <button className="user-button" onClick={() => handleUserClick('Usuario 1')}>Usuario 1</button>
                    <button className="user-button" onClick={() => handleUserClick('Usuario 2')}>Usuario 2</button>
                    <button className="user-button" onClick={() => handleUserClick('Usuario 3')}>Usuario 3</button>
                </div>
            ) : (
                <div className="dashboard">
                    <h2>Bienvenido, {user}</h2>
                    <button className="disconnect-button" onClick={handleDisconnect}>Desconectar</button>

                    {/* Notificaciones */}
                    <div className="notification-bell" onClick={handleNotificationClick}>
                        <span className="bell-icon">🔔</span>
                        {unreadNotifications > 0 && <span className="notification-count">{unreadNotifications}</span>}
                    </div>

                    {/* Mostrar notificaciones */}
                    {showNotifications && (
                        <div className="notifications-dropdown">
                            {notifications.map((notif, idx) => (
                                <div key={idx} className="notification-item">
                                    {notif.message} <span className="notification-time">{notif.timestamp}</span>
                                </div>
                            ))}
                        </div>
                    )}

                    {/* Selección de chat */}
                    <div className="chat-user-selection">
                        <h3>Selecciona un usuario para chatear:</h3>
                        {users.map((u) =>
                            u !== user ? (
                                <button key={u} className="user-chat-button" onClick={() => handleUserSelect(u)}>
                                    {u}
                                </button>
                            ) : null
                        )}
                    </div>

                    {/* Chat */}
                    {selectedUser && (
                        <div className="chat-container">
                            <h3>Chat con {selectedUser}</h3>
                            <div className="chat-box">
                                {chatMessages.map((msg, idx) => (
                                    <div key={idx} className={msg.sender === user ? 'my-message' : 'other-message'}>
                                        <strong>{msg.sender}:</strong> {msg.message} <span className="timestamp">{msg.timestamp}</span>
                                    </div>
                                ))}
                            </div>
                            <textarea
                                value={message}
                                onChange={(e) => setMessage(e.target.value)}
                                placeholder="Escribe un mensaje..."
                            />
                            <button onClick={handleSendMessage}>Enviar</button>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}

export default App;
