// App.js
import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';
import './App.css';

const socket = io('http://localhost:4000');

function App() {
  const [user, setUser] = useState(null);
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [chatMessages, setChatMessages] = useState([]);
  const [message, setMessage] = useState('');
  const [notifications, setNotifications] = useState([]);

  useEffect(() => {
    socket.on('user_update', (users) => {
      setUsers(users);
    });

    socket.on('new_messages', (messages) => {
      setChatMessages((prevMessages) => [...prevMessages, ...messages]);
    });

    socket.on('notification', (notification) => {
      setNotifications((prevNotifications) => [
        ...prevNotifications,
        { text: notification.message, timestamp: new Date().toLocaleTimeString() },
      ]);
    });

    return () => {
      socket.off('user_update');
      socket.off('new_messages');
      socket.off('notification');
    };
  }, [user]);

  const handleUserClick = (username) => {
    setUser(username);
    socket.emit('user_connected', username);
  };

  const handleUserSelect = (selectedUser) => {
    setSelectedUser(selectedUser);
    setChatMessages([]);
  };

  const handleSendMessage = () => {
    if (message.trim() && selectedUser) {
      const chatMessage = {
        sender: user,
        receiver: selectedUser,
        message: message,
        timestamp: new Date().toLocaleTimeString(),
      };
      socket.emit('send_chat_message', chatMessage);
      setMessage('');
      setChatMessages((prevMessages) => [...prevMessages, chatMessage]);
    }
  };

  return (
    <div className="flex h-screen">
      {!user ? (
        <div className="flex justify-center items-center w-full">
          <div className="text-center">
            <h2 className="text-2xl font-semibold mb-4">Selecciona tu usuario</h2>
            {['Usuario 1', 'Usuario 2', 'Usuario 3'].map((username) => (
              <button
                key={username}
                className="bg-blue-500 text-white py-2 px-4 rounded-md m-2"
                onClick={() => handleUserClick(username)}
              >
                {username}
              </button>
            ))}
          </div>
        </div>
      ) : (
        <>
          <div className="w-1/4 bg-gray-100 border-r border-gray-200">
            <div className="p-4">
              <h2 className="font-semibold mb-2">Usuarios Conectados</h2>
              {users.map((u) => (
                <div
                  key={u}
                  className="p-2 bg-purple-100 rounded-lg cursor-pointer"
                  onClick={() => handleUserSelect(u)}
                >
                  {u}
                </div>
              ))}
            </div>
          </div>

          <div className="flex-1 flex flex-col">
            <div className="flex-1 p-4 overflow-y-auto">
              {chatMessages.map((msg, idx) => (
                <div key={idx} className={`mb-4 ${msg.sender === user ? 'text-right' : 'text-left'}`}>
                  <div
                    className={`inline-block p-2 rounded-lg ${
                      msg.sender === user ? 'bg-purple-500 text-white' : 'bg-gray-200 text-black'
                    }`}
                  >
                    <strong>{msg.sender}:</strong> {msg.message}
                  </div>
                </div>
              ))}
            </div>

            <div className="p-4">
              <input
                className="w-full p-2 border rounded mb-2"
                type="text"
                placeholder="Escribe tu mensaje..."
                value={message}
                onChange={(e) => setMessage(e.target.value)}
              />
              <button className="bg-green-500 text-white py-2 px-4 rounded" onClick={handleSendMessage}>
                Enviar
              </button>
            </div>
          </div>

          <div className="w-1/4 bg-gray-100 border-l border-gray-200 p-4">
            <h2 className="font-semibold mb-2">Notificaciones</h2>
            <div className="space-y-2">
              {notifications.map((notification, idx) => (
                <div key={idx} className="p-2 border rounded-lg bg-yellow-100">
                  <div className="text-sm text-gray-600">{notification.timestamp}</div>
                  <div className="text-lg font-medium">{notification.text}</div>
                </div>
              ))}
            </div>
          </div>
        </>
      )}
    </div>
  );
}

export default App;
